<?phpclass Configure_UsersController extends Zend_Controller_Action{    public function init()    {        $this->_helper->layout->setLayout('index');		$this->view->headTitle()			 ->setSeparator(' - ')			 ->prepend('Configuración de Usuarios');    }    public function indexAction()    {		$qry  = new Configure_Model_Queries();		$form = new Configure_Form_ConfigureUsers();		$form->setAction($this->view->url(array(), 'configure-users'));		$this->view->form    = $form;		$this->view->message = trim(implode('', $this->_helper->FlashMessenger->getMessages()));		if( $this->getRequest()->isPost() === true ){			if( $form->isValid($this->getRequest()->getPost()) === true ){				$qry->setParams($form->getValues());				if( $qry->configureAddUsersQry() === true ){					$message = $qry->getMessage();					$this->_helper->FlashMessenger( empty($message) ? ( 'Cuenta añadida el ' . date('Y-m-d g:i:s a') ): $message );					exit( $this->_redirect($this->view->url(array(), 'configure-users')) );				}else{					$this->view->message = 'Ya existe un usuario con esa información';				}			}		}    }}