<?phpclass Configure_Model_Validate_NumEmpl extends Zend_Validate_Abstract{    const ALREADY_EXIST_USER = 'alreadyExistUser';    protected $_messageTemplates = array(        self::ALREADY_EXIST_USER => 'Ya existe un usuario con ese nÃºmero de empleado',    );    public function isValid($value, $context = null)    {        $db = Db_Db::conn();        if (is_array($context) === true) {            $dbQuery = 'SELECT COUNT(*) FROM users WHERE num_empl = ' . $db->quote($value) . ' AND users_id != ' . $db->quote($context['users_id']);            if ($db->fetchOne($dbQuery) > 0) {                $this->_error(self::ALREADY_EXIST_USER);                return false;            }        }        return true;    }}